import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';

import {
  MdAdd,
  MdSearch,
  MdRemoveRedEye,
  MdEdit,
  MdDelete,
  MdKeyboardArrowLeft,
  MdKeyboardArrowRight,
} from 'react-icons/md';

import { toast } from 'react-toastify';

import MoreBtn from '../../components/MoreBtn';

import DeliveryModal from '../../components/DeliveryModal';

import { editDeliveryRequest } from '../../store/modules/delivery/actions';

import {
  Container,
  Title,
  PageActions,
  PageContent,
  Pagination,
} from './styles';

import api from '../../services/api';
import history from '../../services/history';

export default function Deliverys() {
  const [deliveries, setDeliveries] = useState([]);
  const [product, setProduct] = useState('');
  const [visible, setVisible] = useState(false);
  const [orders, setOrders] = useState([]);
  const [page, setPage] = useState(1);

  const dispatch = useDispatch();

  useEffect(() => {
    async function loadDeliveries() {
      const response = await api.get('/delivery', {
        params: {
          product,
          page,
          per_page: 4,
        },
      });

      setDeliveries(response.data);
    }

    loadDeliveries();
  }, [product]);

  async function loadDelivery(id) {
    console.tron.log(id);

    const response = await api.get(`/delivery/${id}`);

    setOrders(response.data);
  }

  function showModal(id) {
    setVisible(!visible);
    loadDelivery(id);
  }

  async function handleDelete(id) {
    try {
      await api.delete(`/delivery/${id}`);
      toast.success('Encomenda deletada!', 1000);
    } catch (error) {
      toast.error('Erro ao deletar o usuário!');
      console.tron.log(error);
    } finally {
      setTimeout(function() {
        history.go(0);
      }, 3500);
    }
  }

  function handleAddPage(page) {
    setPage(page + 1);
  }

  return (
    <>
      <Container>
        <Title>
          <strong>Gerenciando encomendas</strong>
        </Title>

        <PageActions>
          <div>
            <i>
              <MdSearch size={20} />
            </i>

            <input
              type="text"
              placeholder="Buscar por encomenda"
              onChange={e => setProduct(e.target.value)}
            />
          </div>
          <button
            type="button"
            onClick={() => history.push('/entregas_cadastro')}>
            <MdAdd color="#fff" size={20} />
            <strong>Cadastrar</strong>
          </button>
        </PageActions>

        <PageContent>
          <thead>
            <tr>
              <th>ID</th>
              <th>Destinatário</th>
              <th>Entregador</th>
              <th>Cidade</th>
              <th>Estado</th>
              <th>Status</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {deliveries.map(delivery => (
              <>
                <tr key={delivery.id}>
                  <td>{delivery.id}</td>
                  <td>{delivery.recipient.name}</td>
                  <td>
                    <main>
                      <img
                        src={
                          delivery.deliveryman.avatar
                            ? delivery.deliveryman.avatar.url
                            : 'https://api.adorable.io/avatars/abott@adorable.png'
                        }
                        alt="delivery.deliveryman.name"
                      />
                      <span>{delivery.deliveryman.name}</span>
                    </main>
                  </td>
                  <td>{delivery.recipient.city}</td>
                  <td>{delivery.recipient.state}</td>
                  <td>Entregue</td>
                  <td>
                    <MoreBtn>
                      <button
                        type="button"
                        onClick={() => showModal(delivery.id)}>
                        <MdRemoveRedEye color="#7D40E7" size={12} />
                        Visualizar
                      </button>
                      <button
                        type="button"
                        onClick={() =>
                          dispatch(editDeliveryRequest(delivery.id))
                        }>
                        <MdEdit color="#4D85EE" size={12} />
                        Editar
                      </button>
                      <button
                        type="button"
                        onClick={() => handleDelete(delivery.id)}>
                        <MdDelete color="#DE3B3B" size={12} />
                        Excluir
                      </button>
                    </MoreBtn>
                  </td>
                </tr>
                <br />
              </>
            ))}
          </tbody>
        </PageContent>
        <Pagination>
          <button
            type="button"
            onClick={() => handleAddPage(deliveries.page)}
            disabled={page < 2}>
            <MdKeyboardArrowLeft size={20} color="#7d40e7" />
          </button>

          <span>Página {`${page}`}</span>

          <button type="button">
            <MdKeyboardArrowRight size={20} color="#7d40e7" />
          </button>
        </Pagination>
      </Container>
      <DeliveryModal
        visible={visible}
        title="Informações da encomenda"></DeliveryModal>
    </>
  );
}
