import React, { useState, useRef, useEffect } from 'react';
import { useDispatch } from 'react-redux';

import { toast } from 'react-toastify';

import * as Yup from 'yup';

import { MdKeyboardArrowLeft, MdCheck } from 'react-icons/md';

import { Form } from '@rocketseat/unform';

import Input from '../../../components/Form/Input';
import AsyncSelect from '../../../components/Form/AsyncSelect';

import { Container, Title, PageContent, Top, Bottom } from './styles';

import history from '../../../services/history';
import api from '../../../services/api';

export default function DeliverysAdd() {
  const schema = Yup.object().shape({
    recipient_id: Yup.number().required('O destinatário é obrigatório.'),
    deliveryman_id: Yup.number().required('O entregador é obrigatório.'),
    product: Yup.string().required('É preciso definir um produto.'),
  });

  const searchRecipient = inputValue => {
    async function listRecipients() {
      const response = await api.get(`/recipients`, {
        params: { name: inputValue },
      });

      return response.data;
    }

    return listRecipients();
  };

  const loadRecipients = inputValue =>
    new Promise(callback => {
      setTimeout(() => {
        callback(searchRecipient(inputValue));
      }, 100);
    });

  return (
    <>
      <Container>
        <Form schema={schema}>
          <Title>
            <strong>Cadastro de encomendas</strong>

            <aside>
              <button type="button" onClick={() => history.push('/entregas')}>
                <div>
                  <MdKeyboardArrowLeft size={20} color="#fff" />
                  VOLTAR
                </div>
              </button>
              <button type="submit">
                <div>
                  <MdCheck size={18} color="#fff" />
                  SALVAR
                </div>
              </button>
            </aside>
          </Title>

          <PageContent>
            <Top>
              <div>
                <Select label="Destinatário" name="recipient_id" />
              </div>
              <div>
                <DeliverymanSelect label="Entregador" name="deliveryman_id" />
              </div>
            </Top>

            <Bottom>
              <Input
                placeholder="O nome do produto"
                type="text"
                label="Produto"
                name="product"
              />
            </Bottom>
          </PageContent>
        </Form>
      </Container>
    </>
  );
}
