import React, { useState, useEffect } from 'react';

import { MdAdd, MdSearch, MdMoreHoriz } from 'react-icons/md';

import { Container, Title, PageActions, PageContent } from './styles';

import api from '../../services/api';
import history from '../../services/history';

export default function Recipient() {
  const [recipients, setRecipients] = useState([]);
  const [name, setName] = useState('');

  useEffect(() => {
    async function loadRecipients() {
      const response = await api.get('/recipients', {
        params: {
          name,
        },
      });

      setRecipients(response.data);
    }

    loadRecipients();
  }, [name]);

  return (
    <>
      <Container>
        <Title>
          <strong>Gerenciando destinatários</strong>
        </Title>

        <PageActions>
          <div>
            <MdSearch size={20} />
            <input
              onChange={e => setName(e.target.value)}
              type="text"
              placeholder="Buscar por encomenda"
            />
          </div>
          <button type="button" onClick={() => history.push('')}>
            <MdAdd color="#fff" size={20} />
            <strong>Cadastrar</strong>
          </button>
        </PageActions>

        <PageContent>
          <thead>
            <tr>
              <th>ID</th>
              <th>Nome</th>
              <th>Endereço</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {recipients.map(r => (
              <>
                <tr key={r.id}>
                  <td>{r.id}</td>
                  <td>{r.name}</td>
                  <td>
                    `Rua {r.street}, {r.number}, {r.city}, {r.state}`
                  </td>
                  <td>
                    <MdMoreHoriz size={20} color="#666" />
                  </td>
                </tr>
                <br />
              </>
            ))}
          </tbody>
        </PageContent>
      </Container>
    </>
  );
}
