import React, { useState } from 'react';
import { Link } from 'react-router-dom';

import { MdMoreHoriz, MdRemoveRedEye, MdEdit, MdDelete } from 'react-icons/md';

import { Container, Badge, MoreList, More } from './styles';

export default function MoreBtn() {
  const [visible, setVisible] = useState(false);
  const [view, setView] = useState(true);

  function handleToggleVisible() {
    setVisible(!visible);
  }

  return (
    <Container>
      <Badge onClick={handleToggleVisible}>
        <MdMoreHoriz color="#666" size={20} />
      </Badge>

      <MoreList visible={visible}>
        <More hasView={view}>
          <MdRemoveRedEye color="#7D40E7" size={12} />
          <Link to="/">Visualizar</Link>
        </More>
        {/* <More>
          <MdEdit color="#4D85EE" size={12} />
          <Link to="/">Editar</Link>
        </More>
        <More>
          <MdDelete color="#DE3B3B" size={12} />
          <Link to="/">Excluir</Link>
        </More> */}
      </MoreList>
    </Container>
  );
}
