import styled from 'styled-components';

export const Wrapper = styled.div`
  height: 100%;
  background: #7d40e7;
  display: flex;
  justify-content: center;
  align-items: center;
`;

export const Content = styled.div`
  width: 100%;
  max-width: 360px;
  background: #fff;
  text-align: center;
  padding: 20px 10px;
  border-radius: 4px;

  form {
    display: flex;
    flex-direction: column;
    margin-top: 20px;
    max-width: 300px;
    align-items: center;

    strong {
      text-align: left;
      margin-bottom: 10px;
    }

    input {
      border: 1px solid #ddd;
      border-radius: 4px;
      height: 44px;
      padding: 0 15px;
      flex: 1;
    }
  }
`;
