import React, { useRef } from 'react';
import { useDispatch } from 'react-redux';

import * as Yup from 'yup';

import { MdKeyboardArrowLeft, MdCheck } from 'react-icons/md';

import { Form } from '@unform/core';

import Input from '../../../components/Form/Input';
import Select from '../../../components/Form/AsyncSelect';

import { Container, Title, PageContent, Top, Bottom } from './styles';

import history from '../../../services/history';

export default function DeliverysAdd() {
  const formRef = useRef(null);

  const schema = Yup.object().shape({
    recipient_id: Yup.number().required(),
    deliveryman_id: Yup.number().required('O nome é obrigatório'),
    product: Yup.string().required('O e-mail é obrigatório'),
  });

  // const dispatch = useDispatch();

  // function handleSubmit({ id, name, email,  }) {
  //   const data = {
  //     id,
  //     name,
  //     email,
  //     avatar_id,
  //   };

  // dispatch(createDeliverymanRequest(data));
  // }

  return (
    <>
      <Container>
        <Form>
          <Title>
            <strong>Cadastro de encomendas</strong>

            <aside>
              <button type="button" onClick={() => history.push('/entregas')}>
                <div>
                  <MdKeyboardArrowLeft size={20} color="#fff" />
                  VOLTAR
                </div>
              </button>
              <button type="submit">
                <div>
                  <MdCheck size={18} color="#fff" />
                  SALVAR
                </div>
              </button>
            </aside>
          </Title>

          <PageContent>
            <Top>
              <div>
                <span>Nome</span>
                <input type="text" />
              </div>
              <div>
                <span>Entregador</span>
                <input name="deliveryman_id" />
              </div>
            </Top>

            <Bottom>
              <Input
                placeholder="O nome do produto"
                type="text"
                label="Produto"
                name="product"
                formRef={formRef}
              />
            </Bottom>
          </PageContent>
        </Form>
      </Container>
    </>
  );
}
