import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';

import {
  MdAdd,
  MdSearch,
  MdRemoveRedEye,
  MdEdit,
  MdDelete,
} from 'react-icons/md';

import MoreBtn from '../../components/MoreBtn';

import DeliveryModal from '../../components/DeliveryModal';

import { Container, Title, PageActions, PageContent } from './styles';

import api from '../../services/api';
import history from '../../services/history';

export default function Deliverys() {
  const [deliveries, setDeliveries] = useState([]);
  const [product, setProduct] = useState('');
  const [visible, setVisible] = useState(false);
  const [modal, setModal] = useState([]);
  const [order, setOrder] = useState([]);

  const dispatch = useDispatch();

  useEffect(() => {
    async function loadDeliveries() {
      const response = await api.get('/delivery', {
        params: {
          product,
        },
      });

      setDeliveries(response.data);
    }

    loadDeliveries();
  }, [product]);

  function showModal(id) {
    setModal(id);
    setVisible(!visible);
  }

  useEffect(() => {
    async function loadDelivery() {
      const { id } = modal;

      console.tron.log(id);

      const response = await api.get(`/delivery/${id}`);

      setOrder(response.data);
    }

    loadDelivery();
  }, [modal]);

  return (
    <>
      <Container>
        <Title>
          <strong>Gerenciando encomendas</strong>
        </Title>

        <PageActions>
          <div>
            <MdSearch size={20} />
            <input
              type="text"
              placeholder="Buscar por encomenda"
              onChange={e => setProduct(e.target.value)}
            />
          </div>
          <button
            type="button"
            onClick={() => history.push('/entregas_cadastro')}>
            <MdAdd color="#fff" size={20} />
            <strong>Cadastrar</strong>
          </button>
        </PageActions>

        <PageContent>
          <thead>
            <tr>
              <th>ID</th>
              <th>Destinatário</th>
              <th>Entregador</th>
              <th>Cidade</th>
              <th>Estado</th>
              <th>Status</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {deliveries.map(delivery => (
              <>
                <tr key={delivery.id}>
                  <td>{delivery.id}</td>
                  <td>{delivery.recipient.name}</td>
                  <td>
                    <main>
                      <img
                        src={
                          delivery.deliveryman.avatar
                            ? delivery.deliveryman.avatar.url
                            : 'https://api.adorable.io/avatars/abott@adorable.png'
                        }
                        alt="delivery.deliveryman.name"
                      />
                      <span>{delivery.deliveryman.name}</span>
                    </main>
                  </td>
                  <td>{delivery.recipient.city}</td>
                  <td>{delivery.recipient.state}</td>
                  <td>Entregue</td>
                  <td>
                    <MoreBtn>
                      <button
                        type="button"
                        onClick={() => showModal(delivery.id)}>
                        <MdRemoveRedEye color="#7D40E7" size={12} />
                        Visualizar
                      </button>
                      <button type="button">
                        <MdEdit color="#4D85EE" size={12} />
                        Editar
                      </button>
                      <button type="button">
                        <MdDelete color="#DE3B3B" size={12} />
                        Excluir
                      </button>
                    </MoreBtn>
                  </td>
                </tr>
                <br />
              </>
            ))}
          </tbody>
        </PageContent>
        <DeliveryModal visible={visible} title="Informações da encomenda">
          {order.map(o => (
            <div>{o}</div>
          ))}
        </DeliveryModal>
      </Container>
    </>
  );
}
