import React, { useState, useEffect } from 'react';

import { MdRemoveRedEye, MdDelete } from 'react-icons/md';

import MoreBtn from '../../components/MoreBtn';

import api from '../../services/api';

import { Container, Title, PageContent } from './styles';

export default function Problems() {
  const [problems, setProblems] = useState([]);

  useEffect(() => {
    async function loadProblems() {
      const response = await api.get('/problems');

      setProblems(response.data);
    }

    loadProblems();
  }, []);

  return (
    <>
      <Container>
        <Title>
          <strong>Problemas na entrega</strong>
        </Title>

        <PageContent>
          <thead>
            <tr>
              <th>Encomenda</th>
              <th>Problema</th>
              <th className="right">Ações</th>
            </tr>
          </thead>
          <tbody>
            {problems.map(problem => (
              <>
                <tr key={problem.id}>
                  <td className="left">{problem.id}</td>
                  <td>{problem.description}</td>
                  <td className="right">
                    <MoreBtn className="menu">
                      <button type="button">
                        <MdRemoveRedEye color="#4D85EE" size={12} />
                        Visualizar
                      </button>
                      <button type="button">
                        <MdDelete color="#DE3B3B" size={12} />
                        Cancelar
                      </button>
                    </MoreBtn>
                  </td>
                </tr>
                <br />
              </>
            ))}
          </tbody>
        </PageContent>
      </Container>
    </>
  );
}
