import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';

import { MdAdd, MdSearch, MdMoreHoriz } from 'react-icons/md';

import { Container, Title, PageActions, PageContent } from './styles';

import api from '../../services/api';
import history from '../../services/history';

export default function Deliverys() {
  const [deliveries, setDeliveries] = useState([]);
  const [product, setProduct] = useState('');

  const dispatch = useDispatch();

  useEffect(() => {
    async function loadDeliveries() {
      const response = await api.get('/delivery', {
        params: {
          product,
        },
      });

      setDeliveries(response.data);
    }

    loadDeliveries();
  }, [product]);
  return (
    <>
      <Container>
        <Title>
          <strong>Gerenciando encomendas</strong>
        </Title>

        <PageActions>
          <div>
            <MdSearch size={20} />
            <input type="text" placeholder="Buscar por encomenda" />
          </div>
          <button type="button">
            <MdAdd color="#fff" size={20} />
            <strong>Cadastrar</strong>
          </button>
        </PageActions>

        <PageContent>
          <thead>
            <tr>
              <th>ID</th>
              <th>Destinatário</th>
              <th>Entregador</th>
              <th>Cidade</th>
              <th>Estado</th>
              <th>Status</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {deliveries.map(delivery => (
              <>
                <tr key={delivery.id}>
                  <td>{delivery.id}</td>
                  <td>{delivery.recipient.name}</td>
                  <td>
                    <div>
                      <img
                        src={
                          delivery.deliveryman.avatar
                            ? delivery.deliveryman.avatar
                            : 'https://api.adorable.io/avatars/abott@adorable.png'
                        }
                      />
                      <span>{delivery.deliveryman.name}</span>
                    </div>
                  </td>
                  <td>{delivery.recipient.city}</td>
                  <td>{delivery.recipient.state}</td>
                  <td>Entregue</td>
                  <td>
                    <MdMoreHoriz size={20} color="#666" />
                  </td>
                </tr>
                <br />
              </>
            ))}
          </tbody>
        </PageContent>
      </Container>
    </>
  );
}
