import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';

import * as Yup from 'yup';

import { toast } from 'react-toastify';

import DeliveryForm from '../../../components/DeliveryForm';

import api from '../../../services/api';
import history from '../../../services/history';

export default function DeliveryEdit() {
  const [details, setDetails] = useState([]);
  const delivery = useSelector(state => state.delivery.delivery);

  function loadDetails() {
    const { product, recipient_id, deliveryman_id } = delivery;

    setDetails(product, recipient_id, deliveryman_id);
  }

  useEffect(() => {
    loadDetails();
  }, []);

  const schema = Yup.object().shape({
    recipient_id: Yup.number().required('É preciso informar o destinatário.'),
    deliveryman_id: Yup.number().required('É preciso informar o destinatário.'),
    product: Yup.number().required('O produto é obrigatório'),
  });

  return (
    <DeliveryForm
      initialData={delivery}
      title="Edição de encomendas"
      schema={schema}
    />
  );
}
