import styled from 'styled-components';
import { darken } from 'polished';

export const Container = styled.div`
  margin: 0 auto;
  justify-content: center;
  align-items: center;
  max-width: 900px;
  width: 100%;
`;

export const Title = styled.div`
  margin: 40px 0;
  display: flex;
  justify-content: space-between;

  strong {
    font-size: 20px;
    color: #444;
  }

  aside {
    display: block;

    button {
      width: 120px;
      height: 36px;
      justify-content: center;
      align-items: center;
      background: #7d40e7;
      margin-left: 15px;
      color: #fff;
      font-weight: bold;

      border: 0;
      border-radius: 4px;
      transition: background 0.2s;

      &:hover {
        background: ${darken(0.03, '#7d40e7')};
      }

      &:first-child {
        background: #ccc;
      }
    }
  }
`;
