import React, { useState } from 'react';

import { Container, Box } from './styles';

export default function ScreenModal({ children, visible }) {
  const [show, setShow] = useState(false);

  function handleClose(e) {
    setShow(!show);
  }

  if (visible === show) {
    return null;
  }
  return (
    <Container {...visible}>
      <Box>
        <h3>VISUALIZAR PROBLEMA</h3>
        <p>{children}</p>
        <div>
          <button type="button" onClick={e => handleClose(e)}>
            Fechar
          </button>
        </div>
      </Box>
    </Container>
  );
}
