import styled from 'styled-components';

export const Container = styled.div`
  align-items: center;
  margin-bottom: 30px;

  label {
    cursor: pointer;

    &:hover {
      opacity: 0.7;
    }

    img {
      height: 120px;
    }
  }
`;
