import styled from 'styled-components';
import Select from 'react-select/async';

export const Container = styled.div`
  margin: 0 10px;
`;

export const SelectField = styled(Select)`
  margin-top: 5px;
  height: 45px;

  align-content: stretch;

  div {
    display: flex;
    justify-content: center;
    align-items: center;
  }
`;
