import React, { useState } from 'react';
import { MdClose, MdTextFormat } from 'react-icons/md';

import { Container, Content, Info, Dates, Signature } from './styles';

export default function DeliveryInfo({ data, visible }) {
  const [show, setShow] = useState(false);

  function handleClose(e) {
    setShow(!show);
  }
  if (visible === show) {
    return null;
  }

  return (
    <Container {...visible}>
      <Content>
        <Info>
          <div>
            <p>Informações da encomenda</p>
            <button type="button" onClick={e => handleClose(e)}>
              <MdClose size={20} />
            </button>
          </div>
          <div className="info">
            <div>
              <span>Produto: {data.product}</span>
            </div>

            <span className="address">
              {`Rua ${data.recipient.street}, ${data.recipient.number}`}
              <br />
              {data.recipient.city} - {data.recipient.state}
              <br />
              {data.recipient.zipcode}
            </span>
          </div>
        </Info>

        <Dates>
          <div>
            <p>Datas</p>
            <div className="retirada">
              <h4>Retirada:</h4>
              <span>Data</span>
            </div>
            <div>
              <h4>Entrega:</h4>
              <span>Data</span>
            </div>
          </div>
        </Dates>

        <Signature>
          <p>Assinatura do destinatário</p>
          <img
            src={
              data.signature ? (
                data.signature.url
              ) : (
                <div>
                  <MdTextFormat size={30} color="#666" />
                </div>
              )
            }
            alt={Date.signature}
          />
        </Signature>
      </Content>
    </Container>
  );
}
