import styled from 'styled-components';

export const Container = styled.div`
  margin: 0 auto;
  justify-content: center;
  align-items: center;
  max-width: 1000px;
  width: 100%;
`;

export const Title = styled.div`
  margin: 40px 0;

  strong {
    font-size: 20px;
    color: #444;
  }
`;

export const PageContent = styled.table`
  width: 100%;
  margin: 25px 0;
  border-collapse: collapse;

  th,
  td {
    padding: 12px;
    text-align: left;
    border-radius: 4px;
  }

  th.right,
  td.right {
    text-align: right;
  }

  td.left {
    text-align: left;
  }

  td {
    color: #666;
  }
  tbody {
  }

  tbody tr {
    background: #fff;
    border: 0;
  }
`;
