import styled from 'styled-components';

export const Container = styled.div`
  background: #fff;
  padding: 0 10px;
`;

export const Content = styled.div`
  height: 64px;
  max-width: 1200px;
  margin: 0 auto;
  display: flex;
  justify-content: space-between;
  align-items: center;

  nav {
    display: flex;
    align-items: center;

    img {
      margin-right: 20px;
      padding-right: 20px;
      border-right: 1px solid #eee;
    }

    a {
      font-weight: medium;
      font-size: 12px;
      margin-right: 10px;
      color: #999;
    }
  }

  aside {
    display: flex;
    align-items: center;
  }
`;

export const Profile = styled.div`
  display: flex;

  div {
    text-align: right;
    margin-right: 10px;

    strong {
      display: block;
      color: #666;
      font-size: 14px;
    }

    p {
      display: block;
      margin-top: 2px;
      font-size: 12px;
      color: #999;
    }
  }

  button {
    border: 0;
    background: #de3b3b;
    display: flex;
    flex-direction: column;
    height: 40px;
    width: 50px;
  }
`;
