import styled from 'styled-components';

export const Container = styled.div`
  width: 100%;
  max-width: 900px;
  margin: 34px auto;
  span {
    color: #fb6f91;
    align-self: flex-start;
    margin: 0 0 10px;
    font-weight: bold;
  }
  form {
    header {
      display: flex;
      align-self: stretch;
      align-items: center;
      justify-content: space-between;
      h2 {
        font-size: 24px;
        color: #444444;
      }
      div {
        display: flex;
      }
    }
  }
`;

export const Title = styled.div`
  margin: 30px 0 20px;
  display: flex;
  justify-content: space-between;
  strong {
    font-size: 20px;
    color: #444;
  }
  aside {
    display: flex;
    flex-direction: row;
    button {
      width: 100px;
      height: 36px;
      background: #7d40e7;
      margin-left: 15px;
      color: #fff;
      font-weight: medium;
      border: 0;
      border-radius: 4px;
      transition: background 0.2s;
      &:hover {
        background: ${darken(0.03, '#7d40e7')};
      }
      div {
        display: flex;
        flex-direction: row;
        justify-content: center;
        align-items: center;
        margin-right: 5px;
        svg {
          margin-right: 5px;
        }
      }
      &:first-child {
        background: #ccc;
        &:hover {
          background: ${darken(0.03, '#ccc')};
        }
      }
    }
  }
`;

export const InputView = styled.div`
  display: flex;
  flex: 1;
  flex-direction: column;
  strong {
    margin: 15px 7px 4px;
    font-size: 14px;
    color: #444444;
  }
`;

