import React, { useState } from 'react';
import { MdClose } from 'react-icons/md';

import { Container, Content, Info } from './styles';

export default function DeliveryInfo({ data, visible }) {
  const [show, setShow] = useState(false);

  function handleClose(e) {
    setShow(!show);
  }
  if (visible === show) {
    return null;
  }

  return (
    <Container {...visible}>
      <Content>
        <Info>
          <div>
            <p>Informações da encomenda</p>
            <button type="button" onClick={e => handleClose(e)}>
              <MdClose size={20} />
            </button>
          </div>
          <div className="info">
            <h4>Produto: {data.product}</h4>
            <b></b>
            <span className="address">
              {`Rua ${data.recipient.street}, ${data.recipient.number}`}
              <br />
              {data.recipient.city} - {data.recipient.state}
              <br />
              {data.recipient.zipcode}
            </span>
          </div>
        </Info>
      </Content>
    </Container>
  );
}
