import { all, call, put, takeLatest } from 'redux-saga/effects';

import api from '../../../services/api';

import { editDeliverymanSuccess, editDeliverymanFailure } from './actions';

import history from '../../../services/history';

export function* editDeliveryman({ payload }) {
  const { id } = payload;

  try {
    const response = yield call(api.get, `/deliveryman/${id}`);
    const { id, avatar_id, name, email, avatar } = response.data;

    yield put(editDeliverymanSuccess(id, avatar_id, name, email, avatar));

    history.push('/entregadores_edicao');
  } catch ({ response }) {
    yield put(editDeliverymanFailure());
    console.tron.log(response.data.error);
  }
}

export default all([takeLatest('@deliveryman/EDIT_REQUEST', editDeliveryman)]);
