import React, { useState, useRef, useEffect } from 'react';
import { useDispatch } from 'react-redux';

import { toast } from 'react-toastify';

import * as Yup from 'yup';

import { MdKeyboardArrowLeft, MdCheck } from 'react-icons/md';

import { Form } from '@unform/core';

import Input from '../../../components/Form/Input';
import Select from '../../../components/Form/AsyncSelect';

import { Container, Title, PageContent, Top, Bottom } from './styles';

import api from '../../../services/api';
import history from '../../../services/history';

export default function DeliverysAdd() {
  const formRef = useRef(null);

  const schema = Yup.object().shape({
    recipient_id: Yup.number().required('O destinatário é obrigatório.'),
    deliveryman_id: Yup.number().required('O entregador é obrigatório.'),
    product: Yup.string().required('É preciso definir um produto.'),
  });

  async function loadRecipients() {
    const response = await api.get('/recipients');

    const data = response.data.map(r => ({
      label: r.name,
      value: r.id,
    }));

    return data;
  }

  async function loadDeliveryman() {
    try {
      const response = await api.get('/deliveryman');

      return response.data.map(d => ({
        label: d.name,
        value: d.id,
      }));
    } catch ({ response }) {
      console.tron.log(response.data.error);
      toast.error(response.data.error);
      return [];
    }
  }

  async function handleSubmit(data) {
    try {
      await schema.validate(data, {
        abortEarly: false,
      });

      if (delivery && delivery.id) {
        await api.put(
          `deliveryman/${delivery.deliveryman_id}/deliveries/${delivery.id}`,
          data,
        );
        toast.success('Registro editado com sucesso.');
      } else {
        await api.post('deliveries', data);
        toast.success('Registro salvo com sucesso.');
      }

      history.push('/encomendas');
    } catch (err) {
      if (err instanceof Yup.ValidationError) {
        const errorMessages = {};
        err.inner.forEach(error => {
          errorMessages[error.path] = error.message;
        });
        formRef.current.setErrors(errorMessages);
      } else {
        toast.error('Ocorreu um erro ao salvar a encomenda.');
      }
    }
  }

  return (
    <>
      <Container>
        <Form schema={schema}>
          <Title>
            <strong>Cadastro de encomendas</strong>

            <aside>
              <button type="button" onClick={() => history.push('/entregas')}>
                <div>
                  <MdKeyboardArrowLeft size={20} color="#fff" />
                  VOLTAR
                </div>
              </button>
              <button type="submit">
                <div>
                  <MdCheck size={18} color="#fff" />
                  SALVAR
                </div>
              </button>
            </aside>
          </Title>

          <PageContent>
            <Top>
              <div>
                <Select
                  label="Destinatário"
                  name="recipient_id"
                  loadOptions={loadRecipients}
                  formRef={formRef}
                />
              </div>
              <div>
                <Select
                  label="Entregador"
                  name="deliveryman_id"
                  loadOptions={loadDeliveryman}
                  formRef={formRef}
                />
              </div>
            </Top>

            <Bottom>
              <Input
                placeholder="O nome do produto"
                type="text"
                label="Produto"
                name="product"
                formRef={formRef}
              />
            </Bottom>
          </PageContent>
        </Form>
      </Container>
    </>
  );
}
