import React, { useState, useRef, useEffect } from 'react';
import { useDispatch } from 'react-redux';

import { toast } from 'react-toastify';

import * as Yup from 'yup';

import { MdKeyboardArrowLeft, MdCheck } from 'react-icons/md';

import { Form } from '@unform/web';

import Input from '../../../components/Form/Input';
import Select from '../../../components/Form/AsyncSelect';
import DeliverymanSelect from '../../../components/Form/DeliverymanInput';

import { Container, Title, PageContent, Top, Bottom } from './styles';

import api from '../../../services/api';
import history from '../../../services/history';

export default function DeliverysAdd() {
  const schema = Yup.object().shape({
    recipient_id: Yup.number().required('O destinatário é obrigatório.'),
    deliveryman_id: Yup.number().required('O entregador é obrigatório.'),
    product: Yup.string().required('É preciso definir um produto.'),
  });

  async function loadRecipients() {
    const response = await api.get('/recipients');

    const data = response.data.map(r => ({
      label: r.name,
      value: r.id,
    }));

    return data;
  }

  async function loadDeliveryman() {
    try {
      const response = await api.get('/deliveryman');

      return response.data.map(d => ({
        label: d.name,
        value: d.id,
      }));
    } catch ({ response }) {
      console.tron.log(response.data.error);
      toast.error(response.data.error);
      return [];
    }
  }

  return (
    <>
      <Container>
        <Form schema={schema}>
          <Title>
            <strong>Cadastro de encomendas</strong>

            <aside>
              <button type="button" onClick={() => history.push('/entregas')}>
                <div>
                  <MdKeyboardArrowLeft size={20} color="#fff" />
                  VOLTAR
                </div>
              </button>
              <button type="submit">
                <div>
                  <MdCheck size={18} color="#fff" />
                  SALVAR
                </div>
              </button>
            </aside>
          </Title>

          <PageContent>
            <Top>
              <div>
                <Select
                  label="Destinatário"
                  name="recipient_id"
                  loadOptions={loadRecipients}
                />
              </div>
              <div>
                <DeliverymanSelect label="Entregador" name="deliveryman_id" />
              </div>
            </Top>

            <Bottom>
              <Input
                placeholder="O nome do produto"
                type="text"
                label="Produto"
                name="product"
              />
            </Bottom>
          </PageContent>
        </Form>
      </Container>
    </>
  );
}
